# copy-results.ps1\n# Simple script to copy results from Docker container to Windows\n\nWrite-Host \"Copying results from Docker container to Windows...\" -ForegroundColor Green\n\n# Create results directory if it doesn't exist\nif (!(Test-Path \".\\results\")) {\n    New-Item -ItemType Directory -Path \".\\results\" | Out-Null\n}\n\n# Copy results from the Docker volume\ndocker run --rm -v razorfs-stress-results:/results busybox sh -c \"cd /results && tar -cf - .\" | tar -xf - -C .\\results\n\nWrite-Host \"Results copied to .\\results directory\" -ForegroundColor Green\n\n# List the copied files\nGet-ChildItem -Path \".\\results\" -File | Format-Table Name, Length, LastWriteTime